var selfEasyrtcid = "";


function connect() {
    easyrtc.setRoomOccupantListener(convertListToButtons);
    easyrtc.easyApp("easyrtc.audioVideo", "selfVideo", ["callerVideo"], loginSuccess, loginFailure);
 }


function clearConnectList() {
    var otherClientDiv = document.getElementById('otherClients');
    while (otherClientDiv.hasChildNodes()) {
        otherClientDiv.removeChild(otherClientDiv.lastChild);
    }
}


function convertListToButtons (roomName, data, isPrimary) {
    clearConnectList();
    var otherClientDiv = document.getElementById('otherClients');
    for(var easyrtcid in data) {
        var button = document.createElement('button');
	button .style.backgroundColor = "#7070FF";       
	button.onclick = function(easyrtcid) {
            return function() {
                performCall(easyrtcid);
            };
        }(easyrtcid);

        if(easyrtc.idToName(easyrtcid)==GetParam('pass')){
        var label = document.createTextNode(easyrtc.idToName(easyrtcid));
        button.appendChild(label);
        otherClientDiv.appendChild(button);
        }
    }
}


function performCall(otherEasyrtcid) {
    easyrtc.hangupAll();

    var successCB = function() {};
    var failureCB = function() {};
    
    easyrtc.call(otherEasyrtcid, successCB, failureCB);

}


function loginSuccess(easyrtcid) {
    selfEasyrtcid = easyrtcid;
    document.getElementById("iam").innerHTML = "I am " + GetParam('user');
}


function loginFailure(errorCode, message) {
    easyrtc.showError(errorCode, message);
}

function GetParam(name) {

        var start = location.search.indexOf("?" + name + "=");
        if (start < 0) start = location.search.indexOf("&" + name + "=");
        if (start < 0) return '';
        start += name.length + 2;
        var end = location.search.indexOf("&", start) - 1;
        if (end < 0) end = location.search.length;
        var result = '';
        for (var i = start; i <= end; i++) {
            var c = location.search.charAt(i);
            result = result + (c == '+' ? ' ' : c);
        }
        return unescape(result);
    }